import React, { Component } from "react";
import axios from "axios";

class PersonInput extends Component {
  state = {
    name: "",
  };

  handleChange = (event) => {
    this.setState({ name: event.target.value });
  };

  handleSubmit = (event) => {
    event.preventDefault(); // Stop browser reload the page
    const user = {         
      id: this.state.id,
