import React, { Component } from "react";
import axios from "axios";

class PersonInput extends Component {
  state = {
    name: '',
  };

  handleSubmit = () => {
    event.preventDefault();
  }

  render() {
    return (
      <ul>
        {this.state.persons.map((person) => (
          <li key={person.id}>{person.name}</li>
        ))}
      </ul>
    );
  }
}

export default PersonInput;
